-- 塔罗牌占卜数据库结构
-- 注意：请在导入前先手动创建数据库，或者修改下面的数据库名称
-- 示例：CREATE DATABASE IF NOT EXISTS `your_custom_db_name` DEFAULT CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci;
-- 然后：USE `your_custom_db_name`;

-- 管理员表
CREATE TABLE `admins` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `username` varchar(50) NOT NULL,
  `password` varchar(255) NOT NULL,
  `email` varchar(100) DEFAULT NULL,
  `created_at` timestamp DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`),
  UNIQUE KEY `username` (`username`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

-- 塔罗牌表
CREATE TABLE `tarot_cards` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(100) NOT NULL,
  `name_en` varchar(100) NOT NULL,
  `type` enum('major','minor') NOT NULL,
  `suit` varchar(20) DEFAULT NULL,
  `number` int(11) DEFAULT NULL,
  `image` varchar(255) NOT NULL,
  `meaning_upright` text NOT NULL,
  `meaning_reversed` text NOT NULL,
  `keywords` varchar(255) DEFAULT NULL,
  `love_meaning` text DEFAULT NULL,
  `career_meaning` text DEFAULT NULL,
  `health_meaning` text DEFAULT NULL,
  `spiritual_meaning` text DEFAULT NULL,
  `element` varchar(20) DEFAULT NULL,
  `planet` varchar(50) DEFAULT NULL,
  `zodiac` varchar(50) DEFAULT NULL,
  `numerology` text DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

-- 占卜记录表
CREATE TABLE `divination_records` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user_name` varchar(100) DEFAULT NULL,
  `user_qq` varchar(20) DEFAULT NULL,
  `user_wechat` varchar(50) DEFAULT NULL,
  `question` text NOT NULL,
  `spread_type` varchar(50) NOT NULL,
  `cards_drawn` json NOT NULL,
  `interpretation` text NOT NULL,
  `created_at` timestamp DEFAULT CURRENT_TIMESTAMP,
  `updated_at` timestamp DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;



-- 客服消息表
CREATE TABLE `customer_messages` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(100) NOT NULL,
  `qq` varchar(20) DEFAULT NULL,
  `wechat` varchar(50) DEFAULT NULL,
  `subject` varchar(200) NOT NULL,
  `message` text NOT NULL,
  `status` enum('unread','read','replied') DEFAULT 'unread',
  `admin_reply` text DEFAULT NULL,
  `created_at` timestamp DEFAULT CURRENT_TIMESTAMP,
  `replied_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

